const headLabels = [
  "forehead",
  "scalp",
  "l-eye",
  "l-ear",
  "l-cheek",
  "upper-nose",
  "lower-nose",
  "lip",
  "upper-lip",
  "r-cheek",
  "r-eye",
  "r-ear"
];

const backBodyLabels = [
  "back-r-foot",
  "back-l-foot",
  "back-r-calf",
  "back-l-calf",
  "back-r-thigh",
  "back-l-thigh",
  "back-r-forearm",
  "r-bottocks",
  "l-bottocks",
  "back-l-forearmn",
  "back-r-palm",
  "back-l-palm",
  "back-r-shoulder",
  "back-l-shoulder",
  "back-torso",
  "back-neck",
  "head"
];

export default {
  props: {
    form: {
      type: Object,
      required: true
    }
  },

  data() {
    return {
      parts: [],
      headParts: [],
      selected: [],
      oldSelected: [],
      showBack: false,
      showBody: true
    };
  },

  updated() {
    if (this.showBody) {
      // set handlers for front body
      const parts = this.$refs.svg.childNodes;
      Array.from(parts)
        .filter(part => !part.hasAttribute("data-hasHandler"))
        .forEach(part => {
          part.setAttribute("data-hasHandler", "true");
          if (part.id === "head") {
            part.addEventListener("click", () => this.toggleHeadView());
          } else {
            part.addEventListener("click", () => this.handleTouch(part));
          }

          // select those already selected
          if (
            this.form.selected.map(el => el.id).includes(part.id) ||
            this.selected.map(el => el.id).includes(part.id)
          ) {
            part.setAttribute("class", "selected");

            // if selection comes from the parent from, add to local array
            if (!this.selected.map(el => el.id).includes(part.id)) {
              this.selected.push(part);
            }
          }
        });
      this.parts = parts;
    } else {
      // set handlers for head
      const headParts = this.$refs.svg.childNodes;
      Array.from(headParts)
        .filter(part => !part.hasAttribute("data-hasHandler"))
        .forEach(part => {
          part.setAttribute("data-hasHandler", "true");

          part.addEventListener("click", () => this.handleTouch(part));

          // select those already selected
          if (
            this.form.selected.map(el => el.id).includes(part.id) ||
            this.selected.map(el => el.id).includes(part.id)
          ) {
            part.setAttribute("class", "selected");

            // if selection comes from the parent from, add to local array
            if (!this.selected.map(el => el.id).includes(part.id)) {
              this.selected.push(part);
            }
          }
        });
      this.headParts = headParts;
    }
  },

  mounted() {
    const parts = this.$refs.svg.childNodes;

    parts.forEach(part => {
      part.setAttribute("data-hasHandler", "true");

      if (part.id === "head") {
        part.addEventListener("click", () => this.toggleHeadView());
      } else {
        part.addEventListener("click", () => this.handleTouch(part));
      }
      // select those already selected
      if (this.form.selected.map(el => el.id).includes(part.id)) {
        let selectedPart = document.getElementById(part.id);
        part.setAttribute("class", "selected");
        this.selected.push(part);
      }
    });

    // switch to head view if the selection is in head
    for (let i = 0; i < this.form.selected.length; i++) {
      const el = this.form.selected[i];
      if (headLabels.includes(el.id)) {
        this.toggleHeadView();
        break;
      }
    }
  },

  methods: {
    handleTouch(part) {
      if (part.getAttribute("class") === "selected") {
        part.removeAttribute("class");
        part.setAttribute("class", "unselected");
        this.selected = this.selected.filter(p => p.id !== part.id);
      } else {
        part.setAttribute("class", "selected");
        this.selected.push(part);
      }
    },

    toggleHeadView() {
      // cache
      const temp = this.selected;
      this.selected = this.oldSelected;
      this.oldSelected = temp;

      // toggle
      this.showBody = !this.showBody;
    },

    toggleBackView() {
      // cache
      const temp = this.selected;
      this.selected = this.oldSelected;
      this.oldSelected = temp;

      this.clearSelection();

      // toggle
      this.showBack = !this.showBack;
    },

    confirmSelection() {
      // pass selected up the tree
      Object.assign(this.form, {
        selected: this.selected.concat(this.oldSelected)
      });
      this.$parent.close();
    },

    clearSelection() {
      // cleanup
      this.selected = [];
      this.oldSelected = [];

      // wow im dirty

      const parts = this.$refs.svg.childNodes;
      parts.forEach(part => {
        part.removeAttribute("class");
        part.setAttribute("class", "unselected");
      });
    }
  }
};
